import 'package:edusign_v3/models/user_model.dart';
import 'package:edusign_v3/services/storage_service.dart';

class SignatureService {
  static const String _defaultSignture =
      '';

  static Future<String> getSignature(User user) async {
    String? signature = await StorageService.read(key: 'signature_${user.username}');
    return signature ?? _defaultSignture;
  }
}
